  column-count: 1;
  list-style: none;
  padding: 0;
  margin: 0 -15px;
  display: flex;
  flex-wrap: wrap;
  width: var(--maxWidth-full);

  li {
    padding: 5px;
    margin: var(--spacing-1) ;
    box-sizing: border-box;
    border: 1px solid var(--black);
    border-radius: 5px;

    a {
      color: var(--black);
      text-decoration: none;
    }

    h2 {
      font-size: var(--fontSize-2);
    }
  }
  .thumbnail {
    float: left;
  }

  @media screen and (min-width: 512px) {
    column-count: 2;

    li {
      padding: 15px;
      width: 48%;

      h2 {
        font-size: var(--fontSize-3);
      }

      a {
        &:hover h2 {
          text-decoration: underline;
        }
      }
    }
  }
  @media screen and (min-width: 768px) {
    column-count: 3;

    li {
      width: 32%;
    }
  }
`","import * as React from "react"

import { graphql, Link } from "gatsby"
import { PageContext } from "gatsby/internal"
import { mergePosts } from "../utilFunction"
import Layout from "../components/layout"
import Seo from "../components/seo"
import { GatsbyImage } from "gatsby-plugin-image"
import { ContentsListHeader, ContentsOrderedListWrapper } from "../style"
import Pagination from "../components/pagination"

const PageList = ({ pageContext, data, location }: {pageContext: PageContext, data: any, location: Location}) => {
  const posts = mergePosts(data.allMdx, data.allWpPost, data.allFile)
  const title = `記事一覧`

  return (
    <Layout location={location}>
      <ContentsListHeader>
        <h1>{title}</h1>
        {/*<p>{posts} 記事あります</p>*/}
      </ContentsListHeader>
      <ContentsOrderedListWrapper>
        {posts.map(post => {
          return (
            <li key={post.slug}>
              <article
                className="post-list-item"
                itemType="http://schema.org/Article"
              >
                <Link to={post.slug}>
                  <h2>
                    <span>{post.title}</span>
                  </h2>
                  <section>
                    <div><small>
                      <time>{post.date}</time>
                    </small></div>
                    <div className="thumbnail">
                      {typeof post.gatsbyImage === "undefined" ||
                        <GatsbyImage alt={post.altText} image={post.gatsbyImage} className="thumbnail" />
                      }
                    </div>
                    <p
                      dangerouslySetInnerHTML={{ __html: post.excerpt }}
                    />
                  </section>
                </Link>
              </article>
            </li>
          )
        })}
      </ContentsOrderedListWrapper>
      <Pagination maxPage={pageContext.maxPage} current={pageContext.current} />
    </Layout>
  )
}

export default PageList

export const Head = ({ location }: {location: Location}) => {

  return (
    <Seo
      title={`記事一覧`}
      location={location}
    />
  )
}

export const pageQuery = graphql`
  query( $limit: Int!, $skip: Int!) {
    allMdx(
      limit: $limit
      skip: $skip
      sort: { frontmatter: { date: DESC } }
    ) {
      nodes {
        excerpt
        fields {
          slug
        }
        frontmatter {
          title
          date(formatString: "YYYY/MM/DD")
          description
          featuredImagePath
        }
      }
    }
    allWpPost(
      limit: $limit
      skip: $skip
      sort: { date: DESC }
    ) {
      nodes {
        title
        excerpt
        slug
        date(formatString: "YYYY/MM/DD")
        featuredImage{
          node{
            altText
            gatsbyImage(
              width: 100,
              height: 100
              formats: [AUTO, WEBP, AVIF]
              placeholder: BLURRED
            )
          }
        }
      }
    }
    allFile(
      filter: {
        sourceInstanceName: { eq: "images" }
      }
    ) {
      edges {
        node {
          relativePath
          childImageSharp {
            gatsbyImageData(
              width: 100,
              height: 100
              formats: [AUTO, WEBP, AVIF]
              placeholder: BLURRED
            )
          }
        }
      }
    }
  }
`","import { getImage, IGatsbyImageData } from "gatsby-plugin-image"

export function mergePosts(allMdx: AllMdx, allWpPost: AllWpPost, allFile?: AllFile) {
  let allFeaturedImages: { [key: string]: IGatsbyImageData } = {}
  allFile && allFile.edges.forEach(node => {
    allFeaturedImages[node.node.relativePath] = node.node.childImageSharp.gatsbyImageData
  })
  const mdxPosts = allMdx.nodes
  const wpPosts = allWpPost.nodes
  return mdxPosts.map(post => {
    const mdx: CommonPost = {
      title: post.frontmatter.title,
      excerpt: post.excerpt,
      slug: post.fields.slug,
      date: post.frontmatter.date,
      description: post.frontmatter.description,
      altText: post.frontmatter.featuredImagePath,
      gatsbyImage: getImage(allFeaturedImages[post.frontmatter.featuredImagePath || "featured/defaultThumbnail.png"]),
      category: post.frontmatter.category || "",
      tags: post.frontmatter.tags || []
    }
    return mdx
  }).concat(wpPosts.map(post => {
    return {
      title: post.title,
      excerpt: post.excerpt,
      slug: "/" + post.slug,
      date: post.date,
      altText: post.featuredImage?.node.altText || "",
      gatsbyImage: post.featuredImage?.node.gatsbyImage || getImage(allFeaturedImages["featured/defaultThumbnail.png"]),
      category: post.categories?.nodes[0]?.name || "",
      tags: post.tags?.nodes.map(t => t.name) || []
    }
  })).sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime()) as CommonPost[]
}

export function mergePost(mdx?: MdxPost, wpPost?: WpPost, allFile?: AllFile) {
  let allFeaturedImages: { [key: string]: IGatsbyImageData } = {}
  allFile && allFile.edges.forEach(node => {
    allFeaturedImages[node.node.relativePath] = node.node.childImageSharp.gatsbyImageData
  })
  return {
    title: mdx?.frontmatter.title || wpPost?.title,
    excerpt: mdx?.excerpt || wpPost?.excerpt,
    slug: mdx?.fields.slug || "/" + wpPost?.slug,
    date: mdx?.frontmatter.date || wpPost?.date,
    description: mdx?.frontmatter.description || "",
    altText: mdx?.frontmatter.featuredImagePath || wpPost?.featuredImage?.node.altText || "",
    gatsbyImage: getImage(allFeaturedImages[mdx?.frontmatter.featuredImagePath || "featured/defaultThumbnail.webp"])
      || wpPost?.featuredImage?.node.gatsbyImage
      || getImage(allFeaturedImages["featured/defaultThumbnail.webp"])
  } as CommonPost
}
